/**
 * Sample class that may be used as Return type.
 * @example interp.getPointAt(0.2, new Point());
 * @example interp.getPoints(1000, Point);
 */
export default class Point {
    x: number;
    y: number;
    z?: number;
    w?: number;
    constructor(x?: number, y?: number, z?: number, w?: number);
    get 0(): number;
    set 0(x: number);
    get 1(): number;
    set 1(y: number);
    get 2(): number;
    set 2(z: number);
    get 3(): number;
    set 3(w: number);
    get length(): number;
}
