import { pipe, flow } from "fp-ts/function";
import * as O from "fp-ts/Option";
import * as IOO from "fp-ts/IOOption";
import * as NES from "./NonEmptyString";
export const getParam = (k) => () => pipe(process.env[k], O.fromNullable);
export const getParamNonEmpty = flow(getParam, IOO.chainOptionK(NES.fromString));
